mapper.select:
SELECT FUNDING_NO, FUNDING_TITLE, FUNDING_MINI_TITLE, FUNDING_CONTENT, TO_CHAR(TARGET_DONATION,'999,999,999') AS TARGET_DONATION, FUNDING_SEASON, SUBSTR(FUNDING_SEASON,-2) AS MONTH, TO_CHAR(TO_DATE(REPLACE(FUNDING_SEASON,'/',''),'YYYYMM'),'YYYY"년"MM"월"DD"일"') AS STARTDATE, TO_CHAR(LAST_DAY(TO_DATE(REPLACE(FUNDING_SEASON,'/',''),'YYYYMM')),'YYYY"년"MM"월"DD"일"') AS ENDDATE, TRUNC(LAST_DAY(TO_DATE(REPLACE(FUNDING_SEASON,'/',''),'YYYYMM'))-SYSDATE) AS LEFTDATE, SUCCESS_DONATION, FUNDING_ST, FUNDING_THUMBNAIL, DELIVERY_FEE, CATEGORY_NM,
ROUND(FULLPRICE/TARGET_DONATION*100) AS SALES_RATE
FROM FUNDING JOIN CATEGORY USING(FUNDING_CATEGORY) JOIN ( SELECT FUNDING_NO, SUM((REWARD_PRICE*AMOUNT)) AS FULLPRICE FROM REWARD_ORDER JOIN REWARD USING (REWARD_NO) GROUP BY FUNDING_NO) USING (FUNDING_NO) WHERE FUNDING_NO = #{fundingNo}
