mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_NO = #{memberNo}
AND MEMBER_PW = #{memberPw}
AND SECESSION_FL = 'N'

mapper.update:
UPDATE MEMBER SET
MEMBER_PW = #{newPw}
WHERE MEMBER_NO = #{memberNo}
AND SECESSION_FL = 'N'

UPDATE MEMBER SET
SECESSION_FL = 'Y'
WHERE MEMBER_NO = #{memberNo}
