mapper.select:
SELECT BOARD_NO, BOARD_TITLE, BOARD_CONTENT, MEMBER_NO, CASE WHEN SYSDATE - CREATE_DT < 1 THEN TO_CHAR(CREATE_DT, 'HH24:MI') ELSE TO_CHAR(CREATE_DT, 'YYYY-MM-DD') END CREATE_DT, TO_CHAR(CREATE_DT, 'YYYY-MM-DD') BOARD_PERIOD, BOARD_ST, BOARD_NAME AS CATEGORY FROM BOARD B JOIN BOARD_TYPE T ON(CATEGORY= T.BOARD_CD) WHERE B.BOARD_CD =5 AND BOARD_ST = 'N'
AND MEMBER_NO = #{memberNo}
ORDER BY BOARD_NO DESC
